mapper.update:
UPDATE feeds
SET content    = #{content},
    image      = #{images},
    routine_id = #{routineId}
WHERE id = #{id};

mapper.select:
SELECT COUNT(*)
FROM feed_interactions
WHERE feed_id = #{feedId}
  AND user_id = #{userId}
  AND interaction_type = "like";

mapper.delete:
DELETE
FROM feed_interactions
WHERE feed_id = #{feedId}
  AND user_id = #{userId}
  AND interaction_type = "like";

mapper.select:
SELECT user_id, comment, created_at
FROM feed_interactions
WHERE feed_id = #{feedId}
  AND interaction_type = "comment"
ORDER BY created_at DESC;

mapper.update:
UPDATE feed_interactions
SET comment = #{comment}
WHERE id = #{id};
