mapper.update:
UPDATE feeds
SET content = #{content}, image = #{images}, routine_id = #{routineId}
WHERE feed_id = #{feedId};

mapper.select:
SELECT COUNT(*)
FROM feed_interactions
WHERE feed_id = #{feedId} AND user_id = #{userId} AND interaction_type = "like";

mapper.update:
UPDATE feed_interactions
SET comment = #{comment}
WHERE id = #{id};
