mapper.delete:
DELETE
FROM item_details
WHERE item_id = #{itemId}
  AND detail_type = 'image';

mapper.select:
SELECT COUNT(*)
FROM item_details
WHERE item_id = #{itemId}
  AND user_id = #{userId}
  AND detail_type = "like";

