mapper.delete:
DELETE
FROM item_details
WHERE item_id = #{itemId} AND detail_type = 'image';

mapper.update:
UPDATE items
SET title = #{title}, price = #{price}, content = #{content}, updated_at = NOW()
WHERE id = #{id};

mapper.select:
SELECT COUNT(*)
FROM item_details
WHERE item_id = #{itemId}
  AND user_id = #{userId}
  AND detail_type = "like";

mapper.delete:
DELETE
FROM item_details
WHERE item_id = #{itemId}
  AND user_id = #{userId}
  AND detail_type = "like";
